function setCookie(c,d,b){var a=new Date;a.setTime(a.getTime()+24*b*60*60*1000);b="expires="+a.toUTCString();document.cookie=c+"="+d+"; "+b+"; path=/"}function getCookie(e){e=e+"=";var d=decodeURIComponent(document.cookie);var b=d.split(";");for(var a=0;a<b.length;a++){var f=b[a];while(f.charAt(0)==" "){f=f.substring(1)}if(f.indexOf(e)==0){return f.substring(e.length,f.length)}}return""}function checkCookie(a){var b=getCookie(a);return""!=b}function deleteCookie(a){if(checkCookie(a)){setCookie(a,"",-1);return true}else{return false}};